    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_search_by_english_word);

        ArrayAdapter<String> adapter = new ArrayAdapter<String>(getApplicationContext(), android.R.layout.select_dialog_item, arr);
        final AutoCompleteTextView searchText = (AutoCompleteTextView) findViewById(R.id.engText);
        searchText.setThreshold(1);
        searchText.setAdapter(adapter);

        Button searchBtn = (Button) findViewById(R.id.searchButton);

        searchBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                searchString = searchText.getText().toString();

                Intent intent=new Intent(SearchByEnglishWordActivity.this, EnglishSearchResultActivity.class);
                intent.putExtra("searchWord",searchString);
                startActivity(intent);
            }
        });
    }
